function toggleDarkMode(){document.documentElement.getAttribute("data-theme")==="dark"?(document.documentElement.setAttribute("data-theme","light"),document.documentElement.classList.remove("dark"),localStorage.theme="light"):(document.documentElement.setAttribute("data-theme","dark"),document.documentElement.classList.add("dark"),localStorage.theme="dark")}document.addEventListener("DOMContentLoaded",e=>{const t=document.getElementById("dark-mode-toggle");t&&t.addEventListener("click",toggleDarkMode)})